import React, { useState } from 'react';
import './Gallery.css';

const Gallery = () => {
  const [activeFilter, setActiveFilter] = useState('all');

  const galleryItems = [
    {
      id: 1,
      category: 'detailing',
      image: '/img/detailing-1.jpg',
      title: 'Premium Detailing',
      description: 'Showroom finish detailing'
    },
    {
      id: 2,
      category: 'performance',
      image: '/img/performance-1.jpg',
      title: 'Performance Tuning',
      description: 'ECU optimization and upgrades'
    },
    {
      id: 3,
      category: 'maintenance',
      image: '/img/maintenance-1.jpg',
      title: 'Engine Maintenance',
      description: 'Professional engine care'
    },
    {
      id: 4,
      category: 'custom',
      image: '/img/custom-1.jpg',
      title: 'Custom Modifications',
      description: 'Personalized vehicle styling'
    },
    {
      id: 5,
      category: 'paint',
      image: '/img/paint-1.jpg',
      title: 'Paint & Body Work',
      description: 'Professional paint restoration'
    },
    {
      id: 6,
      category: 'detailing',
      image: '/img/detailing-2.jpg',
      title: 'Interior Detailing',
      description: 'Luxury interior restoration'
    },
    {
      id: 7,
      category: 'performance',
      image: '/img/performance-2.jpg',
      title: 'Exhaust Systems',
      description: 'Performance exhaust installation'
    },
    {
      id: 8,
      category: 'custom',
      image: '/img/custom-2.jpg',
      title: 'Wheel Upgrades',
      description: 'Premium wheel installations'
    }
  ];

  const filters = [
    { id: 'all', label: 'All Work' },
    { id: 'detailing', label: 'Detailing' },
    { id: 'performance', label: 'Performance' },
    { id: 'maintenance', label: 'Maintenance' },
    { id: 'custom', label: 'Custom' },
    { id: 'paint', label: 'Paint & Body' }
  ];

  const filteredItems = activeFilter === 'all' 
    ? galleryItems 
    : galleryItems.filter(item => item.category === activeFilter);

  return (
    <section id="gallery" className="section gallery">
      <div className="container">
        <h2 className="section-title">Our Work Gallery</h2>
        <p className="section-subtitle">
          Explore our portfolio of premium automotive work and transformations
        </p>
        
        <div className="gallery-filters">
          {filters.map(filter => (
            <button
              key={filter.id}
              className={`filter-btn ${activeFilter === filter.id ? 'active' : ''}`}
              onClick={() => setActiveFilter(filter.id)}
            >
              {filter.label}
            </button>
          ))}
        </div>
        
        <div className="gallery-grid">
          {filteredItems.map(item => (
            <div key={item.id} className="gallery-item">
              <div className="gallery-image">
                <div className="image-placeholder">
                  <span>🚗</span>
                  <p>{item.title}</p>
                </div>
              </div>
              <div className="gallery-overlay">
                <h3>{item.title}</h3>
                <p>{item.description}</p>
                <button className="btn btn-primary">View Details</button>
              </div>
            </div>
          ))}
        </div>
        
        <div className="gallery-cta">
          <h3>Want to See More?</h3>
          <p>Visit our showroom to see our work in person and discuss your project.</p>
          <button className="btn btn-secondary">Schedule Visit</button>
        </div>
      </div>
    </section>
  );
};

export default Gallery;
